static size_t fgl__GetStringLen(const char *str) {
	size_t result = 0;
	if(str != fgl_null) {
		while(*str++) {
			++result;
		}
	}
	return(result);
}

static void fgl__ClearMemory(void *mem, size_t size) {
	if(mem != fgl_null) {
		uint8_t *p = (uint8_t *)mem;
		while(size > 0) {
			*p++ = 0;
			--size;
		}
	}
}

